package com.mobile.model;//描述设备信息编号,名字,区域,使用状态public class Device {    private String Did;    private String Dname;    private String Dplace;    private boolean Dstatus;    public String getDid() {        return Did;    }    public void setDid(String did) {        Did = did;    }    public String getDname() {        return Dname;    }    public void setDname(String dname) {        Dname = dname;    }    public String getDplace() {        return Dplace;    }    public void setDplace(String dplace) {        Dplace = dplace;    }    public boolean isDstatus() {        return Dstatus;    }    public void setDstatus(boolean dstatus) {        Dstatus = dstatus;    }}