package com.mobile.servlet;import com.mobile.util.DaoFactory;import javax.servlet.ServletException;import javax.servlet.annotation.WebServlet;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;@WebServlet(name = "LoginServlet",urlPatterns = {"/LoginServlet"})public class LoginServlet extends HttpServlet {    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {        if(request.getSession(false)!=null)            request.getSession(false).invalidate();        String eid = request.getParameter("id");        String epwd = request.getParameter("pwd");        if(DaoFactory.getEmployeeDao().employeeLogin(eid,epwd)) {            request.getSession(true).setAttribute("user",DaoFactory.getEmployeeDao().getEmployee(eid));            request.getRequestDispatcher("/index.jsp").forward(request, response);        }        else {            request.setAttribute("eer","√‹¬Î¥ÌŒÛ");            request.getRequestDispatcher("/login.jsp").forward(request, response);        }    }    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {    }}