package com.mobile.servlet.device;import com.mobile.model.Device;import com.mobile.util.DaoFactory;import javax.servlet.ServletException;import javax.servlet.annotation.WebServlet;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;@WebServlet(name = "DeleteDeviceServlet", urlPatterns = {"/DeleteDeviceServlet"})public class DeleteDeviceServlet extends HttpServlet {    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {        dosomething(request,response);    }    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {        dosomething(request,response);    }    protected void dosomething(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {        String did = request.getParameter("ddid");        Device device = null;        if(DaoFactory.getDeviceDao().deleteDevice(did)){            request.getRequestDispatcher("/device.jsp").forward(request, response);        }else{            request.setAttribute("point", "对不起,设备删除失败！！！");            request.getRequestDispatcher("/point.jsp").forward(request, response);        }    }}