package com.mobile.servlet.device;import com.mobile.model.Device;import com.mobile.util.DaoFactory;import com.mobile.util.Pagination;import javax.servlet.ServletException;import javax.servlet.annotation.WebServlet;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;import java.util.List;@WebServlet(name = "DisplayDeviceServlet" ,urlPatterns = {"/DisplayDeviceServlet"})public class DisplayDeviceServlet extends HttpServlet {    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {        dosomething(request, response);    }    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {        dosomething(request, response);    }    private void dosomething(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {        request.setCharacterEncoding("UTF-8");        response.setCharacterEncoding("UTF-8");        String pageNo = request.getParameter("pageNo");        String type = request.getParameter("type");        int page = 1;        if(pageNo!=null) {            page = Integer.parseInt(pageNo);        }        Pagination pagination = new Pagination();        pagination.setPageNo(page);        pagination.setUrl("DisplayDeviceServlet?"+"type="+type+'&');        List<Device> devices = DaoFactory.getDeviceDao().getAllDevice(type,pagination);        request.setAttribute("devices",devices);        request.setAttribute("pagination", pagination);        request.getRequestDispatcher("device.jsp").forward(request,response);    }}