package com.mobile.servlet.employee;import com.mobile.model.Employee;import com.mobile.util.DaoFactory;import javax.servlet.ServletException;import javax.servlet.annotation.WebServlet;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;@WebServlet(name = "InsertEmployeeServlet", urlPatterns = {"/InsertEmployeeServlet"})public class InsertEmployeeServlet extends HttpServlet {    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {        dosomething(request,response);    }    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {        dosomething(request,response);    }    protected void dosomething(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {        request.setCharacterEncoding("UTF-8");        response.setCharacterEncoding("UTF-8");        Employee employee = new Employee();        employee.setEid(request.getParameter("aid"));        employee.setEpwd(request.getParameter("aid"));        employee.setEname(request.getParameter("ename"));        employee.setEsex(request.getParameter("esex"));        employee.setEdept(request.getParameter("edept"));        employee.setEduty(request.getParameter("eduty"));        employee.setEnum(request.getParameter("enum"));        employee.setEemail(request.getParameter("eemail"));        employee.setEtel(request.getParameter("etel"));        employee.setEip(request.getParameter("eip"));        employee.setEauthority(request.getParameter("eauthority"));        if(DaoFactory.getEmployeeDao().insertEmployee(employee)){            request.getRequestDispatcher("/DisplayEmployeeServlet").forward(request, response);        }else{            request.setAttribute("point", "对不起,添加员工失败失败！！！");            request.getRequestDispatcher("/point.jsp").forward(request, response);        }    }}