package com.mobile.servlet.seat;import com.mobile.model.Seat;import com.mobile.util.DaoFactory;import com.mobile.util.Pagination;import javax.servlet.ServletException;import javax.servlet.annotation.WebServlet;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;import java.util.List;@WebServlet(name = "DisplaySeatServlet",urlPatterns = {"/DisplaySeatServlet"})public class DisplaySeatServlet extends HttpServlet {    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {        dosomething(request,response);    }    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {        dosomething(request,response);    }    protected void dosomething(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {        request.setCharacterEncoding("UTF-8");        response.setCharacterEncoding("UTF-8");        String pageNo = request.getParameter("pageNo");        String area = request.getParameter("area");        int page = 1;        if(pageNo!=null) {            page = Integer.parseInt(pageNo);        }        Pagination pagination = new Pagination();        pagination.setPageNo(page);        pagination.setUrl("DisplaySeatServlet?"+"area="+area+'&');        List<Seat> seats = DaoFactory.getSeatDao().getAllSeat(area,pagination);        request.setAttribute("seats",seats);        request.setAttribute("pagination", pagination);        request.getRequestDispatcher("seat.jsp").forward(request,response);    }}