(  in eid varchar(9))  begin    select * from Employee where Employee.Eid = eid;  end  //delimiter ;# 测试set @cc = '201500799';call findEmployee(@cc);select @cc,@dd;select Epwd from Employee where Eid = '201500799' limit 1;################################################################# 插入一个员工信息drop procedure insertEmployee;delimiter //create procedure insertEmployee(  in eid varchar(9),  in epwd varchar(16),  in eauthority varchar(8),  in ename varchar(4),  in esex varchar(3),  in enum char,  in edept varchar(5),  in eduty varchar(5),  in eemail varchar(20),  in etel varchar(13),  in eip varchar(15))  begin    insert into Employee values (eid,epwd,eauthority,ename,esex,                                 enum,edept,eduty,eemail,etel,eip    );  end  //delimiter ;################################################################# 更新一个员工信息drop procedure updateEmployee;delimiter //create procedure updateEmployee(  in eid varchar(9),  in epwd varchar(16),  in eauthority varchar(8),  in ename varchar(4),  in esex varchar(3),  in enum char,  in edept varchar(5),  in eduty varchar(5),  in eemail varchar(20),  in etel varchar(13),  in eip varchar(15))  begin    update Employee set Epwd = epwd, Eauthority = eauthority,      Ename = ename, Esex = esex, Enum = enum,Edept = edept,      Eduty = eduty, Eemail = eemail, Etel = etel,Eip = eip      where Eid = eid;  end  //delimiter ;################################################################# 删除一个员工的信息drop procedure deleteEmployee;delimiter //create procedure deleteEmployee(  in eid varchar(9))  begin    delete from Employee where Eid = eid;  end  //delimiter ;################################################################